const { v4: uuidv4 } = require('uuid');
const crypto = require('crypto');
const base64 = require('base-64');
const redisClient = require('../utils/redis');
const dbClient = require('../utils/db');

class AuthController {
  /**
   * Authenticate user based on the provided authorization header.
   *
   * @param {Object} req - The request object.
   * @param {Object} res - The response object.
   * @return {String} The generated token upon successful user authentication.
   */
  static async getConnect(req, res) {
    const authHeader = req.headers.authorization;

    if (!authHeader || !authHeader.startsWith('Basic ')) {
      return res.status(401).json({ error: 'Unauthorized' });
    }

    const base64Credentials = authHeader.split(' ')[1];
    const credentials = base64.decode(base64Credentials).split(':');
    const email = credentials[0];
    const password = credentials[1];

    const hashedPassword = crypto.createHash('sha1').update(password).digest('hex');

    const user = await dbClient.db.collection('users').findOne({ email, password: hashedPassword });
    if (!user) {
      return res.status(401).json({ error: 'Unauthorized' });
    }

    const token = uuidv4();
    const tokenKey = `auth_${token}`;
    await redisClient.set(tokenKey, user._id.toString(), 24 * 60 * 60);

    res.status(200).json({ token });

    return token;
  }

  static async getDisconnect(req, res) {
    const token = req.headers['x-token'];

    if (!token) {
      return res.status(401).json({ error: 'Unauthorized' });
    }

    const tokenKey = `auth_${token}`;
    const userId = await redisClient.get(tokenKey);

    if (!userId) {
      return res.status(401).json({ error: 'Unauthorized' });
    }

    await redisClient.del(tokenKey);
    res.status(204).send();

    return userId;
  }
}

module.exports = AuthController;

